
public class 연산자문제_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int num1=10;   
	      System.out.printf("%d \n", num1);
	      num1++;
	      System.out.printf("%d \n", num1);
	      ++num1;
	      System.out.printf("%d \n", num1);
	      --num1;
	      System.out.printf("%d \n", num1);
	      num1--;
	      System.out.printf("%d \n", num1);
	      
	      char c='A';
		  System.out.println(c++); // 출력하고 나중에 증가 
	      System.out.println(c);
	}
	}
         /* 결과값은
          * 10
          * 11
          * 12
          * 11
          * 10
          */
